import request from './request'
import type { ApiResult } from './types'

// 鐢ㄤ緥鐢熸垚鐩稿叧绫诲瀷瀹氫箟
export interface CaseGenerationRequest {
  requirementId: number
  layerCode?: string
  methodCode?: string
  templateId?: number
  modelCode?: string
  creatorId?: number
}

export interface CaseGenerationResult {
  taskId: number
  status: string
  message?: string
}

export interface GenerationTask {
  id: number
  requirementId: number
  status: string
  progress?: number
  message?: string
  result?: any
  createTime?: string
  updateTime?: string
}

// 鐢ㄤ緥鐢熸垚API
export const caseGenerationApi = {
  // 鐢熸垚鐢ㄤ緥
  generateTestCases(data: CaseGenerationRequest) {
    return request.post<CaseGenerationResult>('/v1/case-generation/generate', data)
  },

  // 鎵归噺鐢熸垚鐢ㄤ緥
  batchGenerateTestCases(data: CaseGenerationRequest[]) {
    return request.post<CaseGenerationResult>('/v1/case-generation/batch', data)
  },

  // 鏌ヨ鐢熸垚浠诲姟
  getGenerationTask(id: number) {
    return request.get<GenerationTask>(`/v1/case-generation/${id}`)
  }
}

import request from './request'
import type { ApiResult } from './types'

// 鐩镐技鐢ㄤ緥绫诲瀷
export interface SimilarCase {
  caseId: number
  caseCode: string
  caseName: string
  similarity?: number
  [key: string]: any
}

export interface SearchSimilarCasesParams {
  caseText: string
  layerId?: number
  methodId?: number
  topK?: number
  similarityThreshold?: number
}

// 鐢ㄤ緥澶嶇敤API
export const caseReuseApi = {
  // 鍒濆鍖栫敤渚嬪悜閲忚〃
  initCaseVectorTable() {
    return request.post<boolean>('/v1/case-reuse/init')
  },

  // 鏇存柊鐢ㄤ緥鐨勫悜閲忚〃绀篭r
  updateCaseEmbedding(caseId: number) {
    return request.post<boolean>(`/v1/case-reuse/cases/${caseId}/embedding`)
  },

  // 鎼滅储鐩镐技鐢ㄤ緥锛堝熀浜庤涔夌浉浼煎害锛塡r
  searchSimilarCases(params: SearchSimilarCasesParams) {
    return request.post<SimilarCase[]>('/v1/case-reuse/cases/search/similar', null, { params })
  },

  // 鍏抽敭璇嶆绱㈢敤渚媆r
  searchCasesByKeyword(keyword: string, params?: { layerId?: number; methodId?: number; topK?: number }) {
    return request.get<SimilarCase[]>(`/v1/case-reuse/cases/search/keyword/${keyword}`, { params })
  },

  // 鎺ㄨ崘鐩镐技鐢ㄤ緥锛堝熀浜庣幇鏈夌敤渚嬶級
  recommendSimilarCases(caseId: number, topK?: number) {
    return request.get<SimilarCase[]>(`/v1/case-reuse/cases/${caseId}/recommend`, {
      params: { topK }
    })
  },

  // 鍒涘缓鐢ㄤ緥缁勫悎锛堟祴璇曞浠讹級
  createCaseSuite(suiteName: string, caseIds: number[], creatorId?: number) {
    return request.post<number>('/v1/case-reuse/suites', null, {
      params: {
        suiteName,
        caseIds: caseIds.join(','),
        creatorId
      }
    })
  }
}

