export const modelConfigApi = {
  // 鍒涘缓妯″瀷閰嶇疆
  createModelConfig(data: ModelConfig) {
    return request.post<ModelConfig>('/v1/model-configs', data)
  },

  // 鏌ヨ妯″瀷閰嶇疆鍒楄〃
  getModelConfigList(params: ModelConfigListParams = {}) {
