import axios, { AxiosInstance, AxiosRequestConfig, AxiosResponse, InternalAxiosRequestConfig } from 'axios'
import { ElMessage, ElLoading } from 'element-plus'
import type { LoadingInstance } from 'element-plus/es/components/loading/src/loading'
import { useUserStore } from '@/store/user'

// 鍔犺浇鐘舵€佺鐞哱r
let loadingInstance: LoadingInstance | null = null
let loadingCount = 0

// 鏄剧ず鍔犺浇
const showLoading = () => {
  loadingCount++
  if (loadingCount === 1 && !loadingInstance) {
    loadingInstance = ElLoading.service({
      lock: true,
      text: '鍔犺浇涓?..',
      background: 'rgba(0, 0, 0, 0.7)'
    })
  }
}

// 闅愯棌鍔犺浇
const hideLoading = () => {
  loadingCount--
  if (loadingCount <= 0) {
    loadingCount = 0
    if (loadingInstance) {
      loadingInstance.close()
      loadingInstance = null
    }
  }
}

// 璇锋眰鍘婚噸锛氶槻姝㈤噸澶嶈姹俓r
const pendingRequests = new Map<string, AbortController>()

// 鍒涘缓axios瀹炰緥
// 缁熶竴浣跨敤鐩稿璺緞锛岀敱Vite浠ｇ悊锛堝紑鍙戠幆澧冿級鎴朜ginx浠ｇ悊锛堢敓浜х幆澧冿級澶勭悊
const getBaseURL = () => {
  // 濡傛灉璁剧疆浜嗙幆澧冨彉閲忥紝浣跨敤鐜鍙橀噺
  if (import.meta.env.VITE_API_BASE_URL) {
    return import.meta.env.VITE_API_BASE_URL
  }
  // 鍚﹀垯浣跨敤鐩稿璺緞锛岀敱浠ｇ悊澶勭悊锛堝紑鍙戠幆澧僔ite浠ｇ悊锛岀敓浜х幆澧僋ginx浠ｇ悊锛塡r
  return '/api'
}

const service: AxiosInstance = axios.create({
  baseURL: getBaseURL(),
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json'
  }
})

// 璇锋眰鎷︽埅鍣╘r
service.interceptors.request.use(
  (config: InternalAxiosRequestConfig) => {
    // 浠巗tore鑾峰彇token骞舵坊鍔犲埌璇锋眰澶碶r
    const userStore = useUserStore()
    if (userStore.token && config.headers) {
      config.headers.Authorization = `Bearer ${userStore.token}`
    }
    
    // 璇锋眰鍘婚噸锛氬鏋滃瓨鍦ㄧ浉鍚岀殑璇锋眰锛屽彇娑堜箣鍓嶇殑璇锋眰
    const requestKey = `${config.method}-${config.url}-${JSON.stringify(config.params || config.data)}`
    if (pendingRequests.has(requestKey)) {
      const controller = pendingRequests.get(requestKey)!
      controller.abort()
      pendingRequests.delete(requestKey)
    }
    
    // 鍒涘缓鏂扮殑AbortController
    const controller = new AbortController()
    config.signal = controller.signal
    pendingRequests.set(requestKey, controller)
    
    // 璇锋眰瀹屾垚鍚庣Щ闄r
    if (config.signal) {
      config.signal.addEventListener('abort', () => {
        pendingRequests.delete(requestKey)
      })
    }
    
    // 鏄剧ず鍔犺浇鐘舵€侊紙濡傛灉閰嶇疆浜唖howLoading锛塡r
    if (config.showLoading !== false) {
      showLoading()
    }
    
    return config
  },
  (error) => {
    console.error('璇锋眰閿欒:', error)
    hideLoading()
    return Promise.reject(error)
  }
)

// 鍝嶅簲鎷︽埅鍣╘r
service.interceptors.response.use(
  (response: AxiosResponse) => {
    // 闅愯棌鍔犺浇鐘舵€乗r
    if (response.config.showLoading !== false) {
      hideLoading()
    }
    
    // 绉婚櫎宸插畬鎴愮殑璇锋眰
    const requestKey = `${response.config.method}-${response.config.url}-${JSON.stringify(response.config.params || response.config.data)}`
    pendingRequests.delete(requestKey)
    
    const res = response.data
    
    // 濡傛灉杩斿洖鐨勭姸鎬佺爜涓嶆槸200锛屽垯瑙嗕负閿欒
    if (res.code !== 200 && res.code !== 0) {
      ElMessage.error(res.message || '璇锋眰澶辫触')
      return Promise.reject(new Error(res.message || '璇锋眰澶辫触'))
    }
    
    return res
  },
  (error) => {
    // 闅愯棌鍔犺浇鐘舵€乗r
    hideLoading()
    
    // 绉婚櫎宸插け璐ョ殑璇锋眰
    if (error.config) {
      const requestKey = `${error.config.method}-${error.config.url}-${JSON.stringify(error.config.params || error.config.data)}`
      pendingRequests.delete(requestKey)
    }
    
    // 濡傛灉鏄彇娑堢殑璇锋眰锛屼笉鏄剧ず閿欒
    if (axios.isCancel(error)) {
      return Promise.reject(error)
    }
    
    console.error('鍝嶅簲閿欒:', error)
    let message = '璇锋眰澶辫触'
    
    if (error.response) {
      const res = error.response.data
      // 浼樺厛浣跨敤鍚庣杩斿洖鐨勯敊璇俊鎭痋r
      if (res && res.message) {
        message = res.message
      } else {
        switch (error.response.status) {
          case 400:
            message = '璇锋眰鍙傛暟閿欒'
            break
          case 401:
            message = '鏈巿鏉冿紝璇烽噸鏂扮櫥褰?
            // 鍙互鍦ㄨ繖閲岃烦杞埌鐧诲綍椤礬r
            break
          case 403:
            message = '鎷掔粷璁块棶'
            break
          case 404:
            message = '璇锋眰鍦板潃涓嶅瓨鍦?
            break
          case 500:
            message = '鏈嶅姟鍣ㄥ唴閮ㄩ敊璇?
            break
          default:
            message = `璇锋眰澶辫触: ${error.response.status}`
        }
      }
    } else if (error.request) {
      message = '缃戠粶杩炴帴澶辫触锛岃妫€鏌ョ綉缁?
    }
    
    ElMessage.error(message)
    return Promise.reject(error)
  }
)

// 鎵╁睍AxiosRequestConfig绫诲瀷
declare module 'axios' {
  export interface AxiosRequestConfig {
    showLoading?: boolean
  }
}

export default service

import request from './request'
import type { ApiResult } from './types'

// 娴嬭瘯瑕佺偣绫诲瀷
export interface TestPoint {
  point: string
  description?: string
  priority?: string
}

// 涓氬姟瑙勫垯绫诲瀷
export interface BusinessRule {
  rule: string
  description?: string
  type?: string
}

// 闇€姹傚垎鏋愮粨鏋滅被鍨媆r
export interface RequirementAnalysisResult {
  requirementId: number
  requirementName: string
  keywords: string[]
  contentLength: number
  sentenceCount: number
  testPoints: TestPoint[]
  businessRules: BusinessRule[]
  analysisTime: string
}

// 闇€姹傚垎鏋怉PI
export const requirementAnalysisApi = {
  // 鍒嗘瀽闇€姹俓r
  analyzeRequirement(requirementId: number) {
