<script setup lang="ts">
import { ref, reactive, onMounted } from 'vue'
import { ElMessage, ElMessageBox, type FormInstance, type FormRules } from 'element-plus'
import { Plus } from '@element-plus/icons-vue'
import { knowledgeBaseApi, type KnowledgeDocument } from '@/api/knowledgeBase'

// 鍝嶅簲寮忔暟鎹嵁锟?
const loading = ref(false)
const submitLoading = ref(false)
const addDialogVisible = ref(false)
const viewDialogVisible = ref(false)
const formRef = ref<FormInstance>()

const documentList = ref<KnowledgeDocument[]>([])
const searchType = ref<'semantic' | 'keyword'>('semantic')
const searchForm = reactive({
  queryText: '',
  docType: ''
})

const formData = reactive<Partial<KnowledgeDocument>>({
  docCode: '',
  docName: '',
  docType: '',
  docCategory: '',
  docContent: '',
  docUrl: ''
})
const viewData = ref<KnowledgeDocument>({})

// 琛ㄥ崟楠岃瘉瑙勫垯
const formRules: FormRules = {
  docCode: [
    { required: true, message: '璇疯緭鍏ユ枃妗ｇ紪鐮佺爜', trigger: 'blur' }
  ],
  docName: [
    { required: true, message: '璇疯緭鍏ユ枃妗ｅ悕绉?, trigger: 'blur' }
  ],
  docType: [
    { required: true, message: '璇烽€夋嫨鏂囨。绫诲瀷', trigger: 'change' }
  ],
  docContent: [
    { required: true, message: '璇疯緭鍏ユ枃妗ｅ唴瀹?, trigger: 'blur' }
  ]
}

// 鍒濆鍖栫煡璇嗗簱
const handleInit = async () => {
  try {
    await ElMessageBox.confirm('纭畾瑕佸垵濮嬪寲鐭ヨ瘑搴撳悧锛熻繖灏嗗垱寤轰汉蹇呰鐨勮〃缁撴瀯銆?, '鍒濆鍖栫‘璁?, {
      confirmButtonText: '纭畾',
      cancelButtonText: '鍙栨秷',
      type: 'warning'
    })
    
    const response = await knowledgeBaseApi.initKnowledgeBase()
    if (response.data) {
      ElMessage.success('鐭ヨ瘑搴撳垵濮嬪寲鎴愬姛')
    }
  } catch (error: any) {
    if (error !== 'cancel') {
      console.error('鍒濆鍖栫煡璇嗗簱澶辫触', error)
      ElMessage.error('鍒濆鍖栫煡璇嗗簱澶辫触')
    }
  }
}

// 娣诲姞鏂囨。
const handleAdd = () => {
  resetForm()
  addDialogVisible.value = true
}

// 鏌ョ湅鏂囨。
const handleView = (row: KnowledgeDocument) => {
  viewData.value = row
  viewDialogVisible.value = true
}

// 妫€绱㈡枃妗ｅ唴瀹规柟寮忔枃妗ｏ拷?
const handleSearch = async () => {
  if (!searchForm.queryText.trim()) {
    ElMessage.warning('璇疯緭鍏ユ绱㈠唴瀹?)
    return
  }

  loading.value = true
  try {
    let response
    if (searchType.value === 'semantic') {
      response = await knowledgeBaseApi.searchDocuments({
        queryText: searchForm.queryText,
        docType: searchForm.docType || undefined,
        topK: 10,
        similarityThreshold: 0.7
      })
    } else {
      response = await knowledgeBaseApi.searchDocumentsByKeyword(searchForm.queryText, {
        docType: searchForm.docType || undefined,
        topK: 10
      })
    }
    
    if (response.data) {
      documentList.value = response.data
      if (documentList.value.length === 0) {
        ElMessage.info('鏈壘鍒扮浉鍏虫枃妗?)
      }
    }
  } catch (error) {
      console.error('妫€绱㈠け璐?, error)
      ElMessage.error('妫€绱㈠け璐?)
  } finally {
    loading.value = false
  }
}

// 閲嶇疆鎼滅储
const handleReset = () => {
  searchForm.queryText = ''
  searchForm.docType = ''
  documentList.value = []
}

// 鎻愪氦琛ㄥ崟
const handleSubmit = async () => {
  if (!formRef.value) return
  
  await formRef.value.validate(async (valid) => {
    if (valid) {
      submitLoading.value = true
      try {
        await knowledgeBaseApi.addDocument({
          docCode: formData.docCode!,
          docName: formData.docName!,
          docType: formData.docType!,
          docContent: formData.docContent!,
          docCategory: formData.docCategory,
          docUrl: formData.docUrl
        })
        ElMessage.success('娣诲姞鎴愬姛')
        addDialogVisible.value = false
        handleReset()
      } catch (error) {
        console.error('娣诲姞澶辫触:', error)
      } finally {
        submitLoading.value = false
      }
    }
  })
}

// 閲嶇疆琛ㄥ崟
const resetForm = () => {
  Object.assign(formData, {
    docCode: '',
    docName: '',
    docType: '',
    docCategory: '',
    docContent: '',
    docUrl: ''
  })
  formRef.value?.clearValidate()
}

// 瀵硅瘽妗嗗叧闂棴锟?
const handleDialogClose = () => {
  resetForm()
}
</script>

