    { max: 500, message: '鐢ㄤ緥鍚嶇О闀垮害涓嶈兘瓒呰繃500涓瓧绗?, trigger: 'blur' }
  ],
  caseType: [
    { required: true, message: '璇烽€夋嫨鐢ㄤ緥绫诲瀷', trigger: 'change' }
  ],
  casePriority: [
    { required: true, message: '璇烽€夋嫨鐢ㄤ緥浼樺厛绾?, trigger: 'change' }
  ]
}

const reviewFormRules: FormRules = {
  reviewResult: [
    { required: true, message: '璇烽€夋嫨瀹℃牳缁撴灉', trigger: 'change' }
  ]
}

// 璁＄畻灞炴€r
const dialogTitle = computed(() => (isEdit.value ? '缂栬緫鐢ㄤ緥' : '鏂板缓鐢ㄤ緥'))

// 鑾峰彇鐘舵€佹枃鏈琝r
const getStatusText = (status?: string) => {
  const statusMap: Record<string, string> = {
    DRAFT: '鑽夌',
    PENDING_REVIEW: '寰呭鏍?,
    REVIEWED: '宸插鏍?,
    OBSOLETE: '宸插簾寮?
  }
  return statusMap[status || ''] || status || '-'
}

// 鑾峰彇鐘舵€佺被鍨媆r
const getStatusType = (status?: string) => {
  const typeMap: Record<string, string> = {
    DRAFT: 'info',
    PENDING_REVIEW: 'warning',
    REVIEWED: 'success',
    OBSOLETE: 'danger'
  }
  return typeMap[status || ''] || ''
}

// 鑾峰彇浼樺厛绾х被鍨媆r
const getPriorityType = (priority?: string) => {
  const typeMap: Record<string, string> = {
    楂? 'danger',
    涓? 'warning',
    浣? 'info'
  }
  return typeMap[priority || ''] || ''
}

// 鑾峰彇鍙敤鐨勭姸鎬佹祦杞€夐」
const getAvailableStatuses = (currentStatus?: string) => {
  const statusMap: Record<string, Array<{ label: string; value: string }>> = {
    DRAFT: [
      { label: '鎻愪氦瀹℃牳', value: 'PENDING_REVIEW' },
      { label: '搴熷純', value: 'OBSOLETE' }
    ],
    PENDING_REVIEW: [
      { label: '閫€鍥炶崏绋?, value: 'DRAFT' },
      { label: '搴熷純', value: 'OBSOLETE' }
    ],
    REVIEWED: [
      { label: '搴熷純', value: 'OBSOLETE' },
      { label: '閲嶆柊缂栬緫', value: 'DRAFT' }
    ],
    OBSOLETE: [
      { label: '閲嶆柊缂栬緫', value: 'DRAFT' }
    ]
  }
  return statusMap[currentStatus || ''] || []
}

// 鍒ゆ柇鏄惁鍙互鍒犻櫎
const canDelete = (status?: string) => {
  return status !== 'REVIEWED'
}

// 鍔犺浇鐢ㄤ緥鍒楄〃
const loadTestCaseList = async () => {
  loading.value = true
  try {
    const response = await testCaseApi.getTestCaseList({
      page: pagination.page - 1,
      size: pagination.size,
      caseName: searchForm.caseName || undefined,
      caseStatus: searchForm.caseStatus || undefined
    })
    
    if (response.data) {
      testCaseList.value = response.data.content || []
      pagination.total = response.data.totalElements || 0
    }
  } catch (error) {
    console.error('鍔犺浇鐢ㄤ緥鍒楄〃澶辫触:', error)
  } finally {
    loading.value = false
  }
}

// 鎼滅储
const handleSearch = () => {
  pagination.page = 1
  loadTestCaseList()
}

// 閲嶇疆鎼滅储
const handleReset = () => {
  searchForm.caseName = ''
  searchForm.caseStatus = ''
  handleSearch()
}

// 鍒嗛〉澶у皬鏀瑰彉
const handleSizeChange = (size: number) => {
  pagination.size = size
  pagination.page = 1
  loadTestCaseList()
}

// 椤电爜鏀瑰彉
const handlePageChange = (page: number) => {
  pagination.page = page
  loadTestCaseList()
}

// 鏂板缓鐢ㄤ緥
const handleCreate = () => {
  isEdit.value = false
  resetForm()
  dialogVisible.value = true
}

// 缂栬緫鐢ㄤ緥
const handleEdit = async (row: TestCase) => {
  isEdit.value = true
  try {
    const response = await testCaseApi.getTestCaseById(row.id!)
    if (response.data) {
      Object.assign(formData, response.data)
      dialogVisible.value = true
    }
  } catch (error) {
    console.error('鑾峰彇鐢ㄤ緥璇︽儏澶辫触:', error)
  }
}

// 鏌ョ湅璇︽儏
const handleView = async (row: TestCase) => {
  try {
    const response = await testCaseApi.getTestCaseById(row.id!)
    if (response.data) {
      viewData.value = response.data
      viewDialogVisible.value = true
    }
  } catch (error) {
    console.error('鑾峰彇鐢ㄤ緥璇︽儏澶辫触:', error)
  }
}

// 鍒犻櫎鐢ㄤ緥
const handleDelete = async (row: TestCase) => {
  try {
    await ElMessageBox.confirm(
      `纭畾瑕佸垹闄ょ敤渚媆

鍚楋紵`,
      '鍒犻櫎纭',
      {
        confirmButtonText: '纭畾',
        cancelButtonText: '鍙栨秷',
        type: 'warning'
      }
    )
    
    await testCaseApi.deleteTestCase(row.id!)
    ElMessage.success('鍒犻櫎鎴愬姛')
    loadTestCaseList()
  } catch (error: any) {
    if (error !== cancel) {
      console.error('鍒犻櫎鐢ㄤ緥澶辫触:', error)
    }
  }
}

// 鐘舵€佹祦杞琝r
const handleStatusChange = async (row: TestCase, status: string) => {
  try {
    await testCaseApi.updateCaseStatus(row.id!, status)
    ElMessage.success('鐘舵€佹洿鏂版垚鍔?)
    loadTestCaseList()
  } catch (error) {
    console.error('鏇存柊鐘舵€佸け璐?', error)
  }
}

// 瀹℃牳鐢ㄤ緥
const handleReview = (row: TestCase) => {
  currentReviewCase.value = row
  reviewForm.reviewResult = 
  reviewForm.reviewComment = 
  reviewDialogVisible.value = true
}

// 鎻愪氦瀹℃牳
const handleReviewSubmit = async () => {
  if (!reviewFormRef.value) return
  
  await reviewFormRef.value.validate(async (valid) => {
    if (valid && currentReviewCase.value) {
      reviewLoading.value = true
      try {
        await testCaseApi.reviewTestCase(
          currentReviewCase.value.id!,
          reviewForm.reviewResult,
          reviewForm.reviewComment
        )
        ElMessage.success('瀹℃牳鎴愬姛')
        reviewDialogVisible.value = false
        loadTestCaseList()
      } catch (error) {
        console.error('瀹℃牳澶辫触:', error)
      } finally {
        reviewLoading.value = false
      }
    }
  })
}

// 鎻愪氦琛ㄥ崟
const handleSubmit = async () => {
  if (!formRef.value) return
  
  await formRef.value.validate(async (valid) => {
    if (valid) {
      submitLoading.value = true
      try {
        if (isEdit.value) {
          await testCaseApi.updateTestCase(formData.id!, formData)
          ElMessage.success('鏇存柊鎴愬姛')
        } else {
          await testCaseApi.createTestCase(formData)
          ElMessage.success('鍒涘缓鎴愬姛')
        }
        dialogVisible.value = false
        loadTestCaseList()
      } catch (error) {
        console.error('鎻愪氦澶辫触:', error)
      } finally {
        submitLoading.value = false
      }
    }
  })
}

// 瀵煎嚭鐢ㄤ緥
const handleExport = async () => {
  try {
    const response = await testCaseApi.exportTestCases({
      caseName: searchForm.caseName || undefined,
      caseStatus: searchForm.caseStatus || undefined
    })
    
    // 鍒涘缓涓嬭浇閾炬帴
    const blob = new Blob([response.data as Blob], {
      type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
    })
    const url = window.URL.createObjectURL(blob)
    const link = document.createElement('a')
    link.href = url
    link.download = `娴嬭瘯鐢ㄤ緥_${new Date().getTime()}.xlsx`
    document.body.appendChild(link)
    link.click()
    document.body.removeChild(link)
    window.URL.revokeObjectURL(url)
    
    ElMessage.success('瀵煎嚭鎴愬姛')
  } catch (error) {
    console.error('瀵煎嚭澶辫触:', error)
    ElMessage.error('瀵煎嚭澶辫触')
  }
}

// 瀵煎嚭妯℃澘
const handleExportTemplate = async () => {
  try {
    const response = await testCaseApi.exportTemplate()
    
    // 鍒涘缓涓嬭浇閾炬帴
    const blob = new Blob([response.data as Blob], {
      type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
    })
    const url = window.URL.createObjectURL(blob)
    const link = document.createElement('a')
    link.href = url
    link.download = '娴嬭瘯鐢ㄤ緥瀵煎叆妯℃澘.xlsx'
    document.body.appendChild(link)
    link.click()
    document.body.removeChild(link)
    window.URL.revokeObjectURL(url)
    
    ElMessage.success('妯℃澘涓嬭浇鎴愬姛')
  } catch (error) {
    console.error('涓嬭浇妯℃澘澶辫触:', error)
    ElMessage.error('涓嬭浇妯℃澘澶辫触')
  }
}

// 瀵煎叆鐢ㄤ緥
const handleImport = () => {
  importDialogVisible.value = true
  importFileList.value = []
  importResult.value = null
}

// 瀵煎叆鍓嶉獙璇乗r
const beforeImportUpload = (file: File) => {
  const validTypes = ['.xlsx', '.xls']
  const fileExtension = '.' + file.name.split('.').pop()?.toLowerCase()
  
  if (!validTypes.includes(fileExtension)) {
    ElMessage.error('鍙兘涓婁紶Excel鏂囦欢锛?xlsx, .xls锛?)
    return false
  }

  const maxSize = 10 * 1024 * 1024 // 10MB
  if (file.size > maxSize) {
    ElMessage.error('鏂囦欢澶у皬涓嶈兘瓒呰繃10MB')
    return false
  }

  return true
}

// 鑷畾涔変笂浼燶r
const handleImportUpload = async (options: any) => {
  const file = options.file
  importLoading.value = true
  importResult.value = null
  
  try {
    const response = await testCaseApi.importTestCases(file)
    if (response.data) {
      importResult.value = response.data
      if (response.data.failCount === 0) {
        ElMessage.success(`瀵煎叆鎴愬姛锛屽叡瀵煎叆 ${response.data.successCount} 涓敤渚媊)
        loadTestCaseList()
      } else {
        ElMessage.warning(`瀵煎叆瀹屾垚锛屾垚鍔?${response.data.successCount} 涓紝澶辫触 ${response.data.failCount} 涓猔)
      }
    }
  } catch (error: any) {
    console.error('瀵煎叆澶辫触:', error)
    ElMessage.error('瀵煎叆澶辫触锛? + (error.message || '鏈煡閿欒'))
  } finally {
    importLoading.value = false
  }
}

// 瀵煎叆鎴愬姛
const handleImportSuccess = () => {
  // 宸插湪handleImportUpload涓鐞哱r
}

// 瀵煎叆澶辫触
const handleImportError = (error: Error) => {
  console.error('瀵煎叆澶辫触:', error)
  ElMessage.error('瀵煎叆澶辫触锛? + error.message)
  importLoading.value = false
}

// 鎻愪氦瀵煎叆
const submitImport = () => {
  if (importFileList.value.length === 0) {
    ElMessage.warning('璇峰厛閫夋嫨瑕佸鍏ョ殑鏂囦欢')
    return
  }
  importUploadRef.value?.submit()
}

// 璐ㄩ噺璇勪及
const handleQualityAssess = async (row: TestCase) => {
  qualityDialogVisible.value = true
  qualityLoading.value = true
  qualityResult.value = null
  activeCollapse.value = []
  
  try {
    const response = await testCaseQualityApi.assessQuality(row.id!)
    if (response.data) {
      qualityResult.value = response.data
      activeCollapse.value = [completeness, standardization, executability, suggestions]
    }
  } catch (error) {
    console.error('璐ㄩ噺璇勪及澶辫触:', error)
    ElMessage.error('璐ㄩ噺璇勪及澶辫触')
  } finally {
    qualityLoading.value = false
  }
}

// 鑾峰彇璐ㄩ噺绛夌骇绫诲瀷
const getQualityLevelType = (level: string) => {
  const typeMap: Record<string, string> = {
    浼樼: success,
    鑹ソ: success,
    涓€鑸? warning,
    闇€鏀硅繘: danger
  }
  return typeMap[level] || info
}

// 閲嶇疆琛ㄥ崟
const resetForm = () => {
  Object.assign(formData, {
    id: undefined,
    caseCode: undefined,
    caseName: '',
    caseType: '',
    casePriority: '',
    requirementId: undefined,
    preCondition: '',
    testStep: '',
    expectedResult: '',
    caseStatus: 'DRAFT'
  })
  formRef.value?.clearValidate()
}

// 瀵硅瘽妗嗗叧闂璡r
const handleDialogClose = () => {
  resetForm()
}

// 鍒濆鍖朶r
onMounted(() => {
  loadTestCaseList()
})
</script>

