    .header-left {


      .page-title {


        margin: 0;


        font-size: 24px;


        font-weight: 600;


        color: var(--el-text-color-primary);


      }


      .page-subtitle {


        margin: 4px 0 0;


        color: var(--el-text-color-secondary);


        font-size: 14px;


      }


    }


  }





  .search-card {


    margin-bottom: 24px;


    border: none;


    .search-form {


      display: flex;


      flex-wrap: wrap;


      gap: 10px;


      .el-form-item {


        margin-bottom: 0;


        margin-right: 16px;


      }


    }


  }





  .table-card {


    border: none;


    .code-text {


      font-family: monospace;


      color: var(--el-text-color-regular);


      background: rgba(0, 212, 255, 0.1);


      padding: 2px 6px;


      border-radius: 4px;


    }


    .action-buttons {


      display: flex;


      gap: 8px;


       .delete-btn-wrapper {


        display: inline-block;


      }


    }


    .pagination {


      margin-top: 24px;


      display: flex;


      justify-content: flex-end;


    }


  }


}


